import React, {Component} from 'react';
import TodoList from "./components/todoList";
import TodoForm from "./components/TodoForm";
class App extends Component {
    state = {
        todos: [
            {
                todo: 'clean',
                id: Math.random()*1000,
                complete: false
            },

        ]
    }

addToToDos = (input) => {
        const newTodo = {
            todo: input,
            id: Math.random()*1000,
            complete: false
        }
        this.setState({
            ...this.state,
            todos: [...this.state.todos,newTodo]
        })
}

toggleComplete = () => {
        this.setState({
            todos: this.state.todos.map()
        })
}
deleteTodos = (id) => {

            this.setState({
                ...this.state,
                todos: this.state.todos.filter(item => item.id !== id)
            })

}

    render() {
        return (
            <div>
            <TodoList todos={this.state.todos} deleteTodos={this.deleteTodos}/>
            <TodoForm addToToDos={this.addToToDos}/>
                <button onClick={this.deleteTodos}>delete</button>
            </div>
        );
    }
}

export default App;
