export const initialValue = {
    name:'',
    lastName: '',
}


const reducer = (state,action) => {
console.log(action)
    switch (action.type) {
        case 'ADD_NAME':
            return {
                ...state,
                [action.field]: action.payload
            }

        default:
            return state;
    }

}


export default reducer
