import React,{useState} from 'react';
import InputForm from "./components/InputForm";
import Todos from "./components/Todos";
const todos = [
    {
        task: "clean",
        id:Math.random()*1000,
        completed: false
    },]
const App = () => {

    const [state, setState] = useState(todos);

    const addToList = (input) => {
        const newObject = {
            task: input,
            id:Math.random()*1000,
            completed: false
        }
        setState([...state,newObject])

    }
    const toggleCompletion = (id) => {
        setState(state.map(item => {
            if(item.id === id) {
                return {
                    ...item,
                    completed: !item.completed
                }
            }
        } ))

    }




    return (
        <div>
            <InputForm addToList={addToList}/>
        <Todos state={state} toggleCompletion={toggleCompletion} />

        </div>
    );
};

export default App;
