            task: input,
            id: Math.random() * 1000,
            completed: false
        }
    setState([...state,newObj])
    }

    const toggleCompleted = (id) => {
        state.map((item,index) => {
            if(item.id === id) {
                setState([
                    ...state,
                    {

                        ...item,
                        completed: !item.completed
                    }
                ])
                console.log(state)

            }else {
                return item
            }
        })

    }


    return (
        <div>
            <Todos state={state} toggleCompleted={toggleCompleted}/>
            <TodoForm addNewTodo={addNewTodo}/>
        </div>
    );
};

export default App;
