/*ICE
* I: import react from react
* C: components: code that can be used over and over amd usually start with CAPITAL word
* E: export default / export const
* */
import './App.css';
import React,{ useState } from 'react';  // useState here
// import { Button } from 'reactstrap';



function App() {

    const [number, setNumber] = useState(0) // useState

    const increaseByOne = () => {
        setNumber(number + 1)
    }
    const times = () => {
        setNumber(number * 5)
    }
    const zeroMaker =() => {
        setNumber(0)
    }
    console.log(number)
  return (
    <div className="counter">
        <h1>React counter</h1>
        <p className="Click">
            Your count is <span> {number}</span>
        </p>
        <div className="button_container">
            <button onClick={() => increaseByOne} >Add 1</button>
            <button onClick={() => times}>Multiply by 5</button>
            <button onClick={() =>zeroMaker}>Reset</button>
        </div>
    </div>

  );
}



export default App;
