import React, {Component} from 'react';

class TodoForm extends Component {
    state = {
        formValue: ''
    }

    handleChange = (e) => {
        this.setState({
            ...this.state,
            formValue: e.target.value
        })
    }
    handleSubmit = ( e) => {
        e.preventDefault()
        this.props.addToToDos(this.state.formValue)
        this.setState({
            formValue: ''
        })

    }



    render() {

        return (
            <form onSubmit={this.handleSubmit}>
                <input value={this.state.formValue} onChange={this.handleChange}type="text"/>
                <button>add</button>
            </form>
        );
    }
}

export default TodoForm;
