import React, {useState} from 'react';
import TodoForm from "./components/TodoForm";
import Todos from "./components/Todos";
const todos = [
    {
    task: 'clean',
    id: Math.random() * 1000,
    completed: false
    },

]

const App = () => {
    const [state, setState] = useState(todos)

console.log(...state)

    const addNewTodo = (input) => {
        const newObj = {
            task: input,
            id: Math.random() * 1000,
            completed: false
        }
    setState([...state,newObj])
    }

    const toggleCompleted = (id) => {
       setState(state.map(item => {
           if(item.id === id) {return {
               ...item,
               completed: !item.completed
           }

           }else {return item}
       }))
    }

    const erase = () => {
        setState(state.filter(item => !item.completed))

    }


    return (
        <div>
            <Todos state={state} toggleCompleted={toggleCompleted}/>
            <TodoForm addNewTodo={addNewTodo} erase={erase}/>
        </div>
    );
};

export default App;
