import React, {useReducer} from 'react';

import reducer, {initialValue} from "./reducers/reducer";


const App = () => {
    const [state, dispatch] = useReducer(reducer, initialValue)

    return (
        <div>
            <p>{state.name}</p>

            <form>
                <input name={'Name'} placeholder={'Name'} value={state.name} type="text"/>
                <input name={'lastName'} placeholder={'last Name'} value={state.lastName} type="text"/>
            </form>
        </div>
    );
};

export default App;
