export const initialValue = {
    name:'',
    lastName: '',
}


const reducer = (state,action) => {
    switch (action.type) {
        case 'ADD_NAME':
            return {
                ...state,
                name: action.payload
            }
        case 'ADD_LAST_NAME':
            return {
                ...state,
                lastName: action.payload
            }
        default:
            return state;
    }

}


export default reducer
